#!/usr/bin/env python
from public import public
import six

# python 2.x: isinstance(s, basestring)
# python 3.x: isinstance(s, str)

# isinstance(obj, six.string_types)


@public
def isstring(obj):
    if obj is None:
        return
    if isinstance(obj, six.string_types):
        return True
    try:
        int(obj)
        return False
    except ValueError:
        return True
    except TypeError:
        return False
