#!/usr/bin/env python
# -*- coding: utf-8 -*-
from public import public
from vlc.helper import tell
import vlc.fullscreen
import vlc.time
import vlc.volume
import pgrep


@public
def activate():
    tell('activate')


@public
def pid():
    pids = pgrep.pgrep("VLC")
    if pids:
        return pids[0]


@public
def kill():
    if pid():
        tell("quit")


@public
def duration():
    if pid():
        return int(tell('duration of current item'))


@public
def open(path):
    activate()
    tell('open "%s"' % path)


@public
def pause():
    if playing():
        tell('pause')


@public
def path():
    return tell('path of current item')


@public
def play():
    if not playing():
        tell('play')


@public
def playing():
    return "true" in tell('playing')
